import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useEmber } from '../Context/EmberContext.js';

export default function Dashboard() {
  const [barWidths, setBarWidths] = useState([0,0,0,0,0,0,0]);

  const greeting = () => {
    const h = new Date().getHours();
    if (h < 12) return 'Good morning';
    if (h < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const today = new Date().toLocaleDateString('en-US', { weekday:'long', month:'long', day:'numeric' });

  useEffect(() => {
    setTimeout(() => setBarWidths([82,65,70,55,40,72,45]), 600);
  }, []);

  const moodDays = [
    { day:'Mon', width: barWidths[0], type:'joyful',  emoji:'ðŸ˜Š' },
    { day:'Tue', width: barWidths[1], type:'calm',    emoji:'ðŸŒ¿' },
    { day:'Wed', width: barWidths[2], type:'calm',    emoji:'ðŸŒ¿' },
    { day:'Thu', width: barWidths[3], type:'joyful',  emoji:'ðŸ˜Š' },
    { day:'Fri', width: barWidths[4], type:'calm',    emoji:'ðŸŒ™' },
    { day:'Sat', width: barWidths[5], type:'calm',    emoji:'ðŸŒ¿' },
    { day:'Sun', width: barWidths[6], type:'anxious', emoji:'ðŸ’›' },
  ];

